import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService, User } from '../../services/auth.service';
import { UserService, CreateUserRequest, AppUser } from '../../services/user.service';
import { NavbarComponent } from '../navbar/navbar.component';

@Component({
  selector: 'app-users',
  standalone: true,
  imports: [CommonModule, FormsModule, NavbarComponent],
  templateUrl: './users.component.html'
})
export class UsersComponent implements OnInit {
  currentUser: User | null = null;
  users: AppUser[] = [];
  isLoading = false;
  showForm = false;

  username = '';
  email = '';
  password = '';
  fullName = '';
  userRole = 'EDITOR';

  constructor(
    private authService: AuthService,
    private userService: UserService,
    private router: Router
  ) {}

  ngOnInit() {
    this.currentUser = this.authService.getCurrentUser();
    if (!this.currentUser) {
      this.router.navigate(['/login']);
      return;
    }
    this.loadUsers();
  }

  loadUsers() {
    if (!this.currentUser) return;
    this.isLoading = true;
    this.userService.getUsersByCompany(this.currentUser.companyId).subscribe({
      next: (data) => {
        this.users = data;
        this.isLoading = false;
      },
      error: () => {
        this.isLoading = false;
      }
    });
  }

  showCreateForm() {
    this.showForm = true;
    this.username = '';
    this.email = '';
    this.password = '';
    this.fullName = '';
    this.userRole = 'EDITOR';
  }

  cancelForm() {
    this.showForm = false;
  }

  saveUser() {
    if (!this.currentUser) return;

    const user: CreateUserRequest = {
      username: this.username,
      email: this.email,
      password: this.password,
      fullName: this.fullName,
      userRole: this.userRole
    };

    this.userService.createUser(this.currentUser.companyId, user).subscribe({
      next: () => {
        this.loadUsers();
        this.cancelForm();
      },
      error: (err) => alert(err.error?.error || 'Error creating user')
    });
  }

  goBack() {
    this.router.navigate(['/dashboard']);
  }
}
<app-navbar></app-navbar>

<div class="container">
  <h1>Manage Users</h1>

  <div *ngIf="!showForm">
    <button (click)="showCreateForm()">Invite New User</button>
    
    <div *ngIf="isLoading">Loading...</div>

    <table *ngIf="!isLoading && users.length > 0">
      <thead>
        <tr>
          <th>Full Name</th>
          <th>Username</th>
          <th>Email</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let user of users">
          <td>{{ user.fullName }}</td>
          <td>{{ user.username }}</td>
          <td>{{ user.email }}</td>
        </tr>
      </tbody>
    </table>

    <p *ngIf="!isLoading && users.length === 0">No users yet.</p>
  </div>

  <div *ngIf="showForm">
    <h2>Create New User</h2>
    
    <form (ngSubmit)="saveUser()">
      <div class="form-group">
        <label>Full Name:</label>
        <input type="text" [(ngModel)]="fullName" name="fullName" required />
      </div>

      <div class="form-group">
        <label>Username:</label>
        <input type="text" [(ngModel)]="username" name="username" required />
      </div>

      <div class="form-group">
        <label>Email:</label>
        <input type="email" [(ngModel)]="email" name="email" required />
      </div>

      <div class="form-group">
        <label>Password:</label>
        <input type="password" [(ngModel)]="password" name="password" required />
      </div>

      <div class="form-group">
        <label>Role:</label>
        <select [(ngModel)]="userRole" name="userRole">
          <option value="ADMIN">Admin</option>
          <option value="EDITOR">Editor</option>
          <option value="READONLY">Read Only</option>
        </select>
      </div>

      <button type="submit">Save</button>
      <button type="button" (click)="cancelForm()">Cancel</button>
    </form>
  </div>
</div>
