import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { AuthService, User } from '../../services/auth.service';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  currentUser: User | null = null;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    this.authService.currentUser$.subscribe(user => {
      this.currentUser = user;
    });
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }

  goToDashboard() {
    this.router.navigate(['/dashboard']);
  }

  goToProcesses() {
    this.router.navigate(['/processes']);
  }

  goToRoles() {
    this.router.navigate(['/roles']);
  }

  goToUsers() {
    this.router.navigate(['/users']);
  }
}
<nav class="navbar">
  <div class="navbar-left">
    <div class="logo" (click)="goToDashboard()">IPJI</div>
    <div class="nav-links">
      <button (click)="goToDashboard()">Dashboard</button>
      <button (click)="goToProcesses()">Processes</button>
      <button (click)="goToRoles()">Roles</button>
      <button (click)="goToUsers()">Users</button>
    </div>
  </div>

  <div class="navbar-right">
    <span class="user-name">{{ currentUser?.fullName }}</span>
    <span class="company-name">{{ currentUser?.companyName }}</span>
    <button class="logout-btn" (click)="logout()">Logout</button>
  </div>
</nav>
