import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AuthService, RegisterCompanyRequest } from '../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './register.component.html'
})
export class RegisterComponent {
  companyName = '';
  nit = '';
  contactEmail = '';
  adminEmail = '';
  adminPassword = '';
  adminFullName = '';
  errorMessage = '';
  successMessage = '';
  isLoading = false;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  onRegister() {
    if (!this.companyName || !this.nit || !this.contactEmail || 
        !this.adminEmail || !this.adminPassword || !this.adminFullName) {
      this.errorMessage = 'Please fill all fields';
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';
    this.successMessage = '';

    const request: RegisterCompanyRequest = {
      companyName: this.companyName,
      nit: this.nit,
      contactEmail: this.contactEmail,
      adminEmail: this.adminEmail,
      adminPassword: this.adminPassword,
      adminFullName: this.adminFullName
    };

    this.authService.registerCompany(request).subscribe({
      next: () => {
        this.successMessage = 'Company registered successfully! Redirecting to login...';
        setTimeout(() => {
          this.router.navigate(['/login']);
        }, 2000);
      },
      error: (error) => {
        this.errorMessage = error.error?.error || 'Registration failed';
        this.isLoading = false;
      }
    });
  }

  goToLogin() {
    this.router.navigate(['/login']);
  }
}
<div class="register-page">
  <div class="register-header">
    <div class="logo">IPJI</div>
    <div class="header-actions">
      <span>¿Ya estas registrado?</span>
      <button (click)="goToLogin()" class="login-link-btn">Iniciar Sesión</button>
      <span>¿Necesitas ayuda?</span>
      <button class="support-btn">Soporte</button>
    </div>
  </div>

  <div class="register-content">
    <h1>Registro de nuevo usuario</h1>

    <div class="progress-bar">
      <div class="progress-fill"></div>
    </div>

    <form (ngSubmit)="onRegister()">
      <h2>Información Personal</h2>
      <p class="form-subtitle">Por favor completa tu información personal para crear tu cuenta:</p>

      <div class="form-row">
        <div class="form-group">
          <label>Nombre</label>
          <input type="text" [(ngModel)]="adminFullName" name="adminFullName" required placeholder="Andrés" />
        </div>

        <div class="form-group">
          <label>Apellido</label>
          <input type="text" [(ngModel)]="lastName" name="lastName" required placeholder="Gomez" />
        </div>
      </div>

      <div class="form-group">
        <label>Correo electrónico</label>
        <input type="email" [(ngModel)]="adminEmail" name="adminEmail" required placeholder="andres.gomez123@gmail.com" />
      </div>

      <div class="form-group">
        <label>Nombre de Usuario</label>
        <input type="text" [(ngModel)]="username" name="username" required placeholder="andresitogomesito" />
      </div>

      <div class="form-row">
        <div class="form-group">
          <label>Contraseña</label>
          <input type="password" [(ngModel)]="adminPassword" name="adminPassword" required placeholder="hola123*" />
        </div>

        <div class="form-group">
          <label>Conforma tu Contraseña</label>
          <input type="password" [(ngModel)]="confirmPassword" name="confirmPassword" required placeholder="hola123*" />
        </div>
      </div>

      <h3 class="company-section-title">Información de la Empresa</h3>

      <div class="form-group">
        <label>Nombre de la Empresa</label>
        <input type="text" [(ngModel)]="companyName" name="companyName" required placeholder="Mi Empresa" />
      </div>

      <div class="form-group">
        <label>NIT</label>
        <input type="text" [(ngModel)]="nit" name="nit" required placeholder="123456789" />
      </div>

      <div class="form-group">
        <label>Email de Contacto</label>
        <input type="email" [(ngModel)]="contactEmail" name="contactEmail" required placeholder="contacto@miempresa.com" />
      </div>

      <div class="checkbox-group">
        <label>
          <input type="checkbox" [(ngModel)]="acceptPrivacy" name="acceptPrivacy" />
          <span>Acepto que mis datos personales sean tratados de acuerdo con la Política de Privacidad.</span>
        </label>
      </div>

      <div class="checkbox-group">
        <label>
          <input type="checkbox" [(ngModel)]="acceptTerms" name="acceptTerms" />
          <span>He leído y acepto los Términos y Condiciones.</span>
        </label>
      </div>

      <div class="checkbox-group">
        <label>
          <input type="checkbox" [(ngModel)]="acceptNotifications" name="acceptNotifications" />
          <span>Acepto recibir notificaciones de entrenamientos personalizados</span>
        </label>
      </div>

      <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
      <div *ngIf="successMessage" class="success">{{ successMessage }}</div>

      <button type="submit" class="submit-btn" [disabled]="isLoading">
        {{ isLoading ? 'Registrando...' : 'Siguiente' }}
      </button>
    </form>
  </div>
</div>
