import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { WelcomeService } from './welcome.service';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'Welcome Frontend';
  welcomeMessage = '';
  isLoading = false;

  constructor(private welcomeService: WelcomeService) {}

  ngOnInit() {
    this.loadWelcomeMessage();
  }

  loadWelcomeMessage() {
    this.isLoading = true;
    this.welcomeService.getWelcomeMessage().subscribe({
      next: (response) => {
        this.welcomeMessage = response.message;
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error loading welcome message:', error);
        this.welcomeMessage = 'Error loading message from backend';
        this.isLoading = false;
      }
    });
  }
}
<div class="container">
  <h1>{{ title }}</h1>

  <div class="welcome-section">
    <h2>Welcome Message from Backend:</h2>
    <div *ngIf="isLoading" class="loading">
      Loading...
    </div>
    <div *ngIf="!isLoading" class="message">
      {{ welcomeMessage }}
    </div>
    <button (click)="loadWelcomeMessage()" [disabled]="isLoading">
      Refresh Message
    </button>
  </div>
</div>
