import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ProcessService, Process } from '../../services/process.service';
import { ActivityService, Activity } from '../../services/activity.service';
import { GatewayService, Gateway } from '../../services/gateway.service';
import { ArcService, Arc } from '../../services/arc.service';
import { RoleService, Role } from '../../services/role.service';
import { AuthService } from '../../services/auth.service';
import { NavbarComponent } from '../navbar/navbar.component';

@Component({
  selector: 'app-process-details',
  standalone: true,
  imports: [CommonModule, FormsModule, NavbarComponent],
  templateUrl: './process-details.component.html'
})
export class ProcessDetailsComponent implements OnInit {
  processId!: number;
  process: Process | null = null;
  activities: Activity[] = [];
  gateways: Gateway[] = [];
  arcs: Arc[] = [];
  roles: Role[] = [];
  
  activeTab: 'activities' | 'gateways' | 'arcs' = 'activities';
  
  // Activity form
  showActivityForm = false;
  activityName = '';
  activityType = '';
  activityDescription = '';
  activityRoleId: number | null = null;
  editingActivity: Activity | null = null;
  
  // Gateway form
  showGatewayForm = false;
  gatewayName = '';
  gatewayType: 'EXCLUSIVE' | 'PARALLEL' | 'INCLUSIVE' = 'EXCLUSIVE';
  gatewayConditions = '';
  editingGateway: Gateway | null = null;
  
  // Arc form
  showArcForm = false;
  arcSourceType = 'ACTIVITY';
  arcSourceId: number | null = null;
  arcTargetType = 'ACTIVITY';
  arcTargetId: number | null = null;
  editingArc: Arc | null = null;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private processService: ProcessService,
    private activityService: ActivityService,
    private gatewayService: GatewayService,
    private arcService: ArcService,
    private roleService: RoleService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    this.processId = Number(this.route.snapshot.paramMap.get('id'));
    this.loadProcessDetails();
    this.loadRoles();
  }

  loadProcessDetails() {
    this.processService.getProcess(this.processId).subscribe({
      next: (data) => this.process = data
    });
    this.loadActivities();
    this.loadGateways();
    this.loadArcs();
  }

  loadActivities() {
    this.activityService.getActivitiesByProcess(this.processId).subscribe({
      next: (data) => this.activities = data
    });
  }

  loadGateways() {
    this.gatewayService.getGatewaysByProcess(this.processId).subscribe({
      next: (data) => this.gateways = data
    });
  }

  loadArcs() {
    this.arcService.getArcsByProcess(this.processId).subscribe({
      next: (data) => this.arcs = data
    });
  }

  loadRoles() {
    const user = this.authService.getCurrentUser();
    if (user) {
      this.roleService.getRolesByCompany(user.companyId).subscribe({
        next: (data) => this.roles = data
      });
    }
  }

  // Activity methods
  showCreateActivityForm() {
    this.showActivityForm = true;
    this.editingActivity = null;
    this.activityName = '';
    this.activityType = '';
    this.activityDescription = '';
    this.activityRoleId = null;
  }

  showEditActivityForm(activity: Activity) {
    this.showActivityForm = true;
    this.editingActivity = activity;
    this.activityName = activity.name;
    this.activityType = activity.activityType;
    this.activityDescription = activity.description;
    this.activityRoleId = activity.roleId || null;
  }

  saveActivity() {
    const activity: Activity = {
      name: this.activityName,
      activityType: this.activityType,
      description: this.activityDescription,
      roleId: this.activityRoleId || undefined
    };

    if (this.editingActivity) {
      this.activityService.updateActivity(this.editingActivity.id!, activity).subscribe({
        next: () => {
          this.loadActivities();
          this.cancelActivityForm();
        }
      });
    } else {
      this.activityService.createActivity(this.processId, activity).subscribe({
        next: () => {
          this.loadActivities();
          this.cancelActivityForm();
        }
      });
    }
  }

  deleteActivity(activity: Activity) {
    if (!confirm(`Delete activity "${activity.name}"?`)) return;
    this.activityService.deleteActivity(activity.id!).subscribe({
      next: () => this.loadActivities()
    });
  }

  cancelActivityForm() {
    this.showActivityForm = false;
    this.editingActivity = null;
  }

  // Gateway methods
  showCreateGatewayForm() {
    this.showGatewayForm = true;
    this.editingGateway = null;
    this.gatewayName = '';
    this.gatewayType = 'EXCLUSIVE';
    this.gatewayConditions = '';
  }

  showEditGatewayForm(gateway: Gateway) {
    this.showGatewayForm = true;
    this.editingGateway = gateway;
    this.gatewayName = gateway.name;
    this.gatewayType = gateway.gatewayType;
    this.gatewayConditions = gateway.conditions;
  }

  saveGateway() {
    const gateway: Gateway = {
      name: this.gatewayName,
      gatewayType: this.gatewayType,
      conditions: this.gatewayConditions
    };

    if (this.editingGateway) {
      this.gatewayService.updateGateway(this.editingGateway.id!, gateway).subscribe({
        next: () => {
          this.loadGateways();
          this.cancelGatewayForm();
        }
      });
    } else {
      this.gatewayService.createGateway(this.processId, gateway).subscribe({
        next: () => {
          this.loadGateways();
          this.cancelGatewayForm();
        }
      });
    }
  }

  deleteGateway(gateway: Gateway) {
    if (!confirm(`Delete gateway "${gateway.name}"?`)) return;
    this.gatewayService.deleteGateway(gateway.id!).subscribe({
      next: () => this.loadGateways()
    });
  }

  cancelGatewayForm() {
    this.showGatewayForm = false;
    this.editingGateway = null;
  }

  // Arc methods
  showCreateArcForm() {
    this.showArcForm = true;
    this.editingArc = null;
    this.arcSourceType = 'ACTIVITY';
    this.arcSourceId = null;
    this.arcTargetType = 'ACTIVITY';
    this.arcTargetId = null;
  }

  showEditArcForm(arc: Arc) {
    this.showArcForm = true;
    this.editingArc = arc;
    this.arcSourceType = arc.sourceType;
    this.arcSourceId = arc.sourceId;
    this.arcTargetType = arc.targetType;
    this.arcTargetId = arc.targetId;
  }

  saveArc() {
    const arc: Arc = {
      sourceType: this.arcSourceType,
      sourceId: this.arcSourceId!,
      targetType: this.arcTargetType,
      targetId: this.arcTargetId!
    };

    if (this.editingArc) {
      this.arcService.updateArc(this.editingArc.id!, arc).subscribe({
        next: () => {
          this.loadArcs();
          this.cancelArcForm();
        }
      });
    } else {
      this.arcService.createArc(this.processId, arc).subscribe({
        next: () => {
          this.loadArcs();
          this.cancelArcForm();
        }
      });
    }
  }

  deleteArc(arc: Arc) {
    if (!confirm('Delete this arc?')) return;
    this.arcService.deleteArc(arc.id!).subscribe({
      next: () => this.loadArcs()
    });
  }

  cancelArcForm() {
    this.showArcForm = false;
    this.editingArc = null;
  }

  goBack() {
    this.router.navigate(['/processes']);
  }
}
<app-navbar></app-navbar>

<div class="container">
  <h1>Process Details: {{ process?.name }}</h1>

  <div class="tabs">
    <button [class.active]="activeTab === 'activities'" (click)="activeTab = 'activities'">Activities</button>
    <button [class.active]="activeTab === 'gateways'" (click)="activeTab = 'gateways'">Gateways</button>
    <button [class.active]="activeTab === 'arcs'" (click)="activeTab = 'arcs'">Arcs</button>
  </div>

  <!-- Activities Tab -->
  <div *ngIf="activeTab === 'activities'">
    <div *ngIf="!showActivityForm">
      <button (click)="showCreateActivityForm()">Add Activity</button>
      
      <table *ngIf="activities.length > 0">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let activity of activities">
            <td>{{ activity.id }}</td>
            <td>{{ activity.name }}</td>
            <td>{{ activity.activityType }}</td>
            <td>{{ activity.description }}</td>
            <td>
              <button (click)="showEditActivityForm(activity)">Edit</button>
              <button (click)="deleteActivity(activity)">Delete</button>
            </td>
          </tr>
        </tbody>
      </table>
      
      <p *ngIf="activities.length === 0">No activities yet.</p>
    </div>

    <div *ngIf="showActivityForm">
      <h3>{{ editingActivity ? 'Edit Activity' : 'New Activity' }}</h3>
      <form (ngSubmit)="saveActivity()">
        <div class="form-group">
          <label>Name:</label>
          <input type="text" [(ngModel)]="activityName" name="name" required />
        </div>
        <div class="form-group">
          <label>Type:</label>
          <input type="text" [(ngModel)]="activityType" name="type" />
        </div>
        <div class="form-group">
          <label>Description:</label>
          <textarea [(ngModel)]="activityDescription" name="description"></textarea>
        </div>
        <div class="form-group">
          <label>Responsible Role:</label>
          <select [(ngModel)]="activityRoleId" name="roleId">
            <option [value]="null">-- No role --</option>
            <option *ngFor="let role of roles" [value]="role.id">{{ role.name }}</option>
          </select>
        </div>
        <button type="submit">Save</button>
        <button type="button" (click)="cancelActivityForm()">Cancel</button>
      </form>
    </div>
  </div>

  <!-- Gateways Tab -->
  <div *ngIf="activeTab === 'gateways'">
    <div *ngIf="!showGatewayForm">
      <button (click)="showCreateGatewayForm()">Add Gateway</button>
      
      <table *ngIf="gateways.length > 0">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Conditions</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let gateway of gateways">
            <td>{{ gateway.id }}</td>
            <td>{{ gateway.name }}</td>
            <td>{{ gateway.gatewayType }}</td>
            <td>{{ gateway.conditions }}</td>
            <td>
              <button (click)="showEditGatewayForm(gateway)">Edit</button>
              <button (click)="deleteGateway(gateway)">Delete</button>
            </td>
          </tr>
        </tbody>
      </table>
      
      <p *ngIf="gateways.length === 0">No gateways yet.</p>
    </div>

    <div *ngIf="showGatewayForm">
      <h3>{{ editingGateway ? 'Edit Gateway' : 'New Gateway' }}</h3>
      <form (ngSubmit)="saveGateway()">
        <div class="form-group">
          <label>Name:</label>
          <input type="text" [(ngModel)]="gatewayName" name="name" required />
        </div>
        <div class="form-group">
          <label>Type:</label>
          <select [(ngModel)]="gatewayType" name="type">
            <option value="EXCLUSIVE">Exclusive</option>
            <option value="PARALLEL">Parallel</option>
            <option value="INCLUSIVE">Inclusive</option>
          </select>
        </div>
        <div class="form-group">
          <label>Conditions:</label>
          <textarea [(ngModel)]="gatewayConditions" name="conditions"></textarea>
        </div>
        <button type="submit">Save</button>
        <button type="button" (click)="cancelGatewayForm()">Cancel</button>
      </form>
    </div>
  </div>

  <!-- Arcs Tab -->
  <div *ngIf="activeTab === 'arcs'">
    <div *ngIf="!showArcForm">
      <button (click)="showCreateArcForm()">Add Arc</button>
      
      <table *ngIf="arcs.length > 0">
        <thead>
          <tr>
            <th>ID</th>
            <th>Source Type</th>
            <th>Source ID</th>
            <th>Target Type</th>
            <th>Target ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let arc of arcs">
            <td>{{ arc.id }}</td>
            <td>{{ arc.sourceType }}</td>
            <td>{{ arc.sourceId }}</td>
            <td>{{ arc.targetType }}</td>
            <td>{{ arc.targetId }}</td>
            <td>
              <button (click)="showEditArcForm(arc)">Edit</button>
              <button (click)="deleteArc(arc)">Delete</button>
            </td>
          </tr>
        </tbody>
      </table>
      
      <p *ngIf="arcs.length === 0">No arcs yet.</p>
    </div>

    <div *ngIf="showArcForm">
      <h3>{{ editingArc ? 'Edit Arc' : 'New Arc' }}</h3>
      <form (ngSubmit)="saveArc()">
        <div class="form-group">
          <label>Source Type:</label>
          <select [(ngModel)]="arcSourceType" name="sourceType">
            <option value="ACTIVITY">Activity</option>
            <option value="GATEWAY">Gateway</option>
          </select>
        </div>
        <div class="form-group">
          <label>Source ID:</label>
          <input type="number" [(ngModel)]="arcSourceId" name="sourceId" required />
        </div>
        <div class="form-group">
          <label>Target Type:</label>
          <select [(ngModel)]="arcTargetType" name="targetType">
            <option value="ACTIVITY">Activity</option>
            <option value="GATEWAY">Gateway</option>
          </select>
        </div>
        <div class="form-group">
          <label>Target ID:</label>
          <input type="number" [(ngModel)]="arcTargetId" name="targetId" required />
        </div>
        <button type="submit">Save</button>
        <button type="button" (click)="cancelArcForm()">Cancel</button>
      </form>
    </div>
  </div>
</div>
